declare module "utils/function" {
    /**
     * The `isString` function checks if a given value is of type string and returns a boolean value.
     * @param {any} value - The `value` parameter is the value that you want to check for being a string.
     * It can be of any type.
     * @returns The `isString` function returns a boolean value indicating whether the provided `value`
     * is of type string.
     */
    export function isString(value: any): boolean;
  }
  